INSERT INTO `resource_venues` (`id`, `cat_id`, `venue`, `short_name`, `location`, `city`, `county`, `state`, `info`, `phone`, `web`, `lat`, `lng`) VALUES
(1, 1, 'Waterman\'s Family Farm', 'Waterman\'s Farm Mkt', '7010 East Raymond Street', 'Indianapolis', 'Marion County', 'IN', 'Open late May through early November, 8 AM to 8 PM daily, with a wide range of seasonal products from strawberries to sweet corn to peppers, tomatoes, cantaloupes, greens, and more. ', '317-356-6995', '', '39.742226', '-86.047047'),
(2, 1, 'Farmers Market at the Fairgrounds in Greenfield', 'Greenfield Fairgrounds', 'Hancock County Fairgrounds, 620 North Apple Street', 'Greenfield', 'Hancock County', 'IN', 'In addition to the regular season market every Sat, 8 AM to 12 PM, May 2 to Oct. 31, the market is also open on Wed, 8 AM to 12 PM, July 1 to Sept. 30.', '317-697-0508', '', '39.791179', '-85.757087'),
(3, 1, 'Wilson Farm Market', 'Wilson Farm Mkt', '1720 East 256th Street', 'Arcadia', 'Hamilton County', 'IN', 'An excellent farm market with a wide variety of produce, vegetables, fruits, breads, fudge, pies, beverages, doughnuts, fried chicken, deli, bakery, and more. Open daily 9 AM to 6 PM. On US Hwy 31 north of Westfield.\r\n', '317-758-5734', '', '40.160816', '-86.127047'),
(4, 1, 'Original Farmers Market at the Indianapolis City Market', 'City Market', 'Indianapolis City Market, 222 East Market Street', 'Indianapolis', 'Marion County', 'IN', 'An outdoor agricultural product-based, producer-only farmers market, rain or shine, that takes up an entire city block in the street outside City Market. Every Wed, 9:30 AM to 1:30 PM, May to October. Free admission.', '317-634-9266', '', '39.768812', '-86.153413'),
(5, 1, 'Whitley County Farmers Market', 'Whitley Co. Farm Mkt', 'Parkview Whitley Hospital, 1260 Indiana Hwy 205', 'Columbia City', 'Whitley County', 'IN', 'Every Wednesday, May 13 to Oct. 14: 4-7 PM.', '260-248-8131', '', '41.158237', '-85.464875'),
(6, 1, 'West Lafayette Farmers Market', 'W. Lafayette Farm Mkt', '3001 N Salisbury Street', 'West Lafayette', 'Tippecanoe County', 'IN', 'Produce, flowers, baked goods, vegetables, fruits, and lots more. Every Wednesday, May to Oct: 4-7 PM in May, Sept and Oct; 4-8 PM in June, July and Aug.', '765-447-9999', '', '40.460672', '-86.915588'),
(7, 1, 'South Bend Farmers Market', 'So. Bend Farm Mkt', '1105 Northside Blvd', 'South Bend', 'Saint Joseph County', 'IN', 'This venerable market has been open since 1924 and features nearly 100 vendors offering a wide variety of products and foods, plus an in-house cafe. Open 7 AM to 6 PM on Tue/Thu, 7 AM to 2 PM on Fri, 7 AM to 3 PM on Sat. Info: 574-282-1259.\r\nb.	Location: South Bend Farmers Market, 1105 Northside Blvd, South Bend, StJoseph County.\r\n', '574-282-1259', '', '41.665899', '-86.234270'),
(8, 1, 'Statehouse Market at the Indiana State Capitol Building', 'Statehouse Mkt', 'Indiana State Capitol Building, 200 West Washington Street', 'Indianapolis', 'Marion County', 'IN', 'Numerous vendors and food trucks will be present at this market, every Thu, May 28 to Oct. 1, 10:30 AM to 1:30 PM. Free admission.', '317-232-0200', '', '39.768622', '-86.162703'),
(9, 1, 'Purdue Farmers Market', 'Purdue Farm Mkt', 'North of Memorial Mall', 'West Lafayette', 'Tippecanoe County', 'IN', 'Produce, flowers, baked goods, vegetables, fruits, and lots more. Every Thursday, May to Oct, 11 AM to 3 PM.', '765-447-9999', '', '40.425752', '-86.914270'),
(10, 1, 'Fortville Farmers Market', 'Fortville Farm Mkt', 'Church of the Nazarene, 701 South Maple Street', 'Fortville', 'Hancock County', 'IN', 'A wide variety of local produce, vegetables, meats, flowers, and other products, in the church parking lot, every Thu, 4-7 PM, May 7 to Sept. 29. Free admission.', '317-485-0410', '', '39.928920', '-85.845247'),
(11, 1, 'Farmers Market by Mainstreet in Greensburg', 'Greensburg Farm Mkt', 'South Side of Courthouse Square', 'Greensburg', 'Decatur County', 'IN', 'Fresh local produce, baked goods, flowers, and lots more. Every Friday, 2-6 PM, May 1 to Oct. 30. Booth space available.', '812-222-0037', '', '39.336787', '-85.483487'),
(12, 1, 'Bean Blossom Farmers Market', 'Bean Blossom Farm Mkt', 'St. David\'s Episcopal Church, State Route 45 and State Route 135 North', 'Bean Blossom', 'Brown County', 'IN', 'Locally grown fruits and vegetables, local meats, herbs, honey, baked goods, handmade crafts, live music, and more. Fridays, 4-7 PM. Free admission.', '812-988-1038', '', '39.266727', '-86.249512'),
(13, 1, 'Pierceton Farmers Market', 'Pierceton Farm Mkt', 'Brower Park', 'Pierceton', 'Kosciusko County', 'IN', 'Every Friday in Brower Park on State Road 13, May 22 to Oct. 30, 4-7 PM. Free admission.', '574-268-4860', '', '41.200228', '-85.705076'),
(14, 1, 'Friday on the Farm at Traders Point Creamery', 'Traders Pt. Creamery', 'Traders Point Creamery, 9101 Moore Road', 'Zionsville', 'Marion County', 'IN', 'Dinner on the Garden Deck, Umbrella Bar Lounge on the Lawn, live music, Green Market farmers market, Dairy Bar, Petting Zoo (6-9 PM), and more. Every Friday, June to August, 5-9 PM, rain or shine.', '317-733-1700', '', '39.917287', '-86.287663'),
(15, 1, 'Howe Farmers Market', 'Howe Farm Mkt', 'Town Park, State Road 120', 'Howe', 'LaGrange County', 'IN', 'A good selection of fruits, crafts, organic food, local specialties and vegetables. Every Saturday, May 2 to Oct. 31, 6 AM to 12 PM.', '260-562-3882', '', '41.720266', '-85.420656'),
(16, 1, 'Henry County Farmers Market', 'Henry Co. Farm Mkt', 'Henry County Courthouse, Broad and Main Streets', 'New Castle', 'Henry County', 'IN', 'On the east side of the courthouse every Saturday, 8 AM to 12 PM, May 30 to Oct. 17, rain or shine (unless severe weather is imminent).', '765-610-7527', '', '39.930634', '-85.370363'),
(17, 1, 'Farmers Market at Cumberland Town Hall', 'Cumberland Farm Mkt.', 'Cumberland Town Hall, 11501 East Washington Street', 'Cumberland', 'Marion County', 'IN', 'Every Saturday, 8 AM to 12 PM, May 2 to Oct. 31.', '317-894-6203', '', '39.775746', '-85.961188'),
(18, 1, 'Chesterton European Market', 'Chesterton Euro Mkt', 'Broadway and 3rd Street', 'Chesterton', 'Porter County', 'IN', 'An outdoor family/artisanal market held in historic downtown Chesterton, every Sat, May 2 to Oct. 31: 8 AM to 2 PM. Info: See Web site below or call 219-926-5513.\r\nb.	Location: Chesterton European Market, Broadway and 3rd Street, Chesterton, Porter County.\r\n', '219-926-5513', '', '41.610922', '-87.054504'),
(19, 1, 'Bloomington Farmers Market', 'Bloomington Farm Mkt', '401 North Morton Street', 'Bloomington', 'Monroe County', 'IN', 'Every Saturday: April 4 to Sept. 26, 8 AM to 1 PM, plus Oct. 3 to Nov. 21, 9 AM to 1 PM, and Nov. 28, 10 AM to 3 PM.', '812-349-3738', '', '39.169378', '-86.536135'),
(20, 1, 'Fishers Farmers Market', 'Fishers Farm Mkt.', 'Fishers City Hall, Nickel Plate Park', 'Fishers', 'Hamilton County', 'IN', 'A wide variety of vendors and products, plus live music, every Saturday, 8 AM to 12 PM, May 2 to Sept. 26. Nickel Plate Park is directly behind City Hall. Info: 317-578-0700.\r\nb.	Location: Fishers City Hall, Nickel Plate Park, Fishers, Hamilton County.\r\n', '317-578-0700', '', '39.959441', '-86.017108'),
(21, 1, 'Franklin Farmers Market', 'Franklin Farm Mkt', 'Jefferson and Jackson Streets', 'Franklin', 'Johnson County', 'IN', 'More than 50 vendors offer a wide variety of produce, fruit, vegetables, handcrafted items, flowers, and more. Every Sat, May 9 to Oct. 3, 8 AM to 12 PM.', '317-346-1258', '', '39.480486', '-86.056494'),
(22, 1, 'Clinton County Farmers Market', 'Clinton Co. Farm Mkt', 'Walnut and Jackson Streets', 'Frankfort', 'Clinton County', 'IN', 'Every Saturday, June 6 to Oct. 17, 8 AM to 12 PM, at the intersection of State Roads 39 and 28 in downtown Frankfort.', '765-659-0773', '', '40.279679', '-86.510696'),
(23, 1, 'Broad Ripple Farmers Market', 'Broad Ripple Farm Mkt', 'Broad Ripple High School, 1115 Broad Ripple Avenue', 'Indianapolis', 'Marion County', 'IN', 'Over 70 vendors offer a wide variety of produce, fruit, vegetables, baked goods, flowers, meat, dairy, eggs, and lots more. Every Saturday, 8 AM to 12 PM, May 2 to Nov. 21, rain or shine.', '317-251-2782', '', '39.868981', '-86.138071'),
(24, 1, 'The Market at Hague', 'Market At Hague', 'Lawrence North High School, 7802 Hague Road', 'Indianapolis', 'Marion County', 'IN', 'Formerly Binford Farmers Market. 100+ vendors including produce, artisan, meat and fish, bakeries, nurseries, wineries and more. Every Sat, May 2 to Oct. 31, 8 AM to 12 PM.', '317-690-8559', '', '39.895800', '-86.034042'),
(25, 1, 'Carmel Farmers Market', 'Carmel Farm Mkt.', 'The Center for the Performing Arts, 1 Center Green', 'Carmel', 'Hamilton County', 'IN', 'Numerous farmers, vendors, and producers offer a huge variety of products every Saturday, May 2 to Sept. 26, 8-11:30 AM, on the lawn in front of the Carmel Palladium. Free admission.', '317-843-3800', '', '39.968756', '-86.130159'),
(26, 1, 'Minnetrista Farmers Market', 'Minnetrista Farm Mkt', 'Minnetrista, 1200 North Minnetrista Pkwy', 'Muncie', 'Delaware County', 'IN', 'Featuring a wide variety of locally-sourced products, every Saturday, May 16 to Oct. 31, 8 AM to 12 PM.', '765-282-4848', '', '40.205943', '-85.389613'),
(27, 1, 'HomeGrown Orleans Farmers Market', 'Home Grown Orleans Mkt', 'Congress Square', 'Orleans', 'Orange County', 'IN', 'Over 50 local vendors sell locally grown produce, baked goods and handmade crafts. Every Sat, May 16 to Oct. 31, 8 AM to 12 PM. Free admission.', '812-653-0977', '', '38.662259', '-86.453215'),
(28, 1, 'Danville Farmers Market', 'Danville Farm Mkt', 'Courthouse Square (Jefferson St.)', 'Danville', 'Hendricks County', 'IN', 'Every Saturday, 8 AM to 12 PM, through Sept. 12. A producer-only market: all goods sold are grown, made or baked by the vendor (no resale items allowed). On Jefferson Street on the west side of Courthouse Square.', '317-745-0670', '', '39.760292', '-86.524902'),
(29, 1, 'Downtown Farmers Market', 'Downtown Farm Mkt', 'Mulberry and Washington Streets', 'Kokomo', 'Howard County', 'IN', 'A producers market with some two dozen vendors offering a wide variety of produce, fruit, vegetables, plus entertainment, workshops, kids area, and more. Every Sat, 9 AM to 1 PM, Apr. 25 to Oct. 10.', '765-210-8235', '', '40.488307', '-86.133680'),
(30, 1, 'Zionsville Farmers Market', 'Zionsville Farm Mkt', 'Main Street and Hawthorne', 'Zionsville', 'Boone County', 'IN', 'Locally grown fruits and vegetables, breads, pastries, cheeses, fresh meat, egss, flowers, and lots more. Every Saturday, May 16 to Sept. 26, 8-11 AM. Free admission.', '317-873-3836', '', '39.949076', '-86.261177'),
(31, 1, 'Greenfield Farmers Market', 'Greenfield Farm Mkt', 'Public Parking Lot, 100 West Main Street', 'Greenfield', 'Hancock County', 'IN', 'Every Sat, May 16 to Sept. 26, 9 AM to 12 PM, in the public parking lot adjacent to Griggsby\'s Station on the south side of the street, across from Lincoln Square Pancake House.', '', '', '39.785182', '-85.770475'),
(32, 1, 'YLNI Barr Street Market', 'YNLI Barr St. Mkt', '302 East Berry Street', 'Fort Wayne', 'Allen County', 'IN', 'Fresh and unique local produce, plants, meats, baked goods and homemade crafts in a cultural outdoor setting. Every Sat, 9 AM to 1 PM, May 16 to Sept. 12. Free admission.', '260-424-3700', '', '41.079579', '-85.136043'),
(33, 1, 'Huddleston Farmers Market', 'Huddleston Farm Mkt', 'Huddleston Farmhouse, 838 National Rd (US Highway 40)', 'Cambridge City', 'Wayne County', 'IN', 'Produce and wares from local farmers, gardeners and artists, every Sat, 9 AM to 1 PM, July 4 to Aug. 29. Located on the west side of Cambridge City at Indiana Landmarks Eastern Regional Office.', '765-478-3172', '', '39.812199', '-85.187294'),
(34, 1, 'Morgan County Farmers Market', 'Morgan Co. Farm Mkt', 'Courthouse Square, Morgan and Main Streets', 'Martinsville', 'Morgan County', 'IN', 'Locally grown produce, plants, craft items, and more. Every Sat, May 2 to Sept. 26, 9 AM to 1 PM. Free admission.', '765-346-5611', '', '39.427864', '-86.428577'),
(35, 1, 'Munster Farmers Market', 'Munster Farm Mkt', ' Munster Town Hall, 1005 Ridge Road', 'Munster', 'Lake County', 'IN', 'Produce, food, music, bath and body products, jewelry, and more. Every Sunday, May 3 to Oct. 11, 8 AM to 2 PM CST. Free admission.', '219-836-7275', '', '41.561802', '-87.504687'),
