(39, 1, 'Irvington Farmers Market', 'Irvington Farm Mkt', 'Ellenberger Park, 5301 East St. Clair Street', 'Indianapolis', 'Marion County', 'IN', 'This market is scheduled for the 2nd Sunday of each month, 12-3 PM, June to October: June 7, July 12, Aug. 9, Sept. 13, and Oct. 11.', '317-327-7176', '', '39.776543', '-86.076642'),
(40, 1, 'Central Park Plaza Farmers Market', 'Central Park Farm Mkt', '68 Lafayette Street', 'Valparaiso', 'Porter County', 'IN', 'An outdoor market with local produce, handmade products, plants and flowers. Live musical entertainment. Every Tue, June 2 to Oct. 17: 8 AM to 2 PM, plus every Sat, 8 AM to 12 PM (CST).', '219-926-2255', '', '41.467264', '-87.062256'),
(41, 1, 'Beautify Beech Grove Farmers Market', 'Beautify Beech Grove Mkt', 'BG Emporium, 3535 South Emerson Avenue', 'Beech Grove', 'Marion County', 'IN', 'A public farmers market open every Tuesday, 3-7 PM, May 19 to Oct. 27. Free admission.', '317-850-6898', '', '39.715185', '-86.080653'),
