(43, 1, 'Artisan Foodworks Market', 'Artisan Foods Mkt', '240sweet, 9600 N US Hwy 31', 'Columbus', 'Bartholomew County', 'IN', 'A monthly food and artisan market featuring a changing schedule of vendors. Each month has a different theme. July 19, Aug. 16, Sept. 20: Sundays, 12-3 PM.', '812-372-9898', '', '39.289150', '-85.951017'),
(44, 1, 'Johnny Appleseed Festival', 'Johnny Appleseed Fest.', 'Johnny Appleseed Park, 5800 Harry Baals Drive', 'Fort Wayne', 'Allen County', 'IN', 'A two-day period festival with more than 200 booths, good food, crafts, antiques, music, farmers market, kids area, reenactments, demos, and lots more. Free admission. Sept. 19-20: hours and activities vary daily.', '260-427-6003', '', '41.111521', '-85.120858'),
(45, 1, 'Alexandria Farmers\' Market', 'Alexandria Farm Mkt', '100 Church Street', 'Alexandria', 'Madison County', 'IN', 'This is a seasonal open air market featuring locally grown produce, eggs and grass-fed meat, fresh baked goods, handmade soaps\r\nand items from local artists.  Saturdays, 8am - noon, June - October.', '765-623-6994', '', '40.263596', '-85.674444'),
